@import url('https://fonts.googleapis.com/css2?family=Averta:wght@400&display=swap');

.print-format {
    font-size: 12pt;
}
.topbar {
    padding: 5px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: relative;
    /*margin-bottom:-50px;*/
}

.logo {
    margin-right: 10px;
    margin-bottom: auto;
}

.logo img {
    height: auto;
    width: 200px;
}

.quotation {
    font-size: 25px;
    margin-left: auto;
    font-family: 'Averta', sans-serif;
}

.brand {
    text-align: right;
    position: relative;
}

.brand-name {
    font-weight: bold;
    font-family: 'Averta', sans-serif;
    font-size: 14px;
}

.line {
    border-top: 1px solid black;
    margin-top: auto;
    width: 100%;
}

.line-bottom {
    position: absolute;
    bottom: -10px;
    left: 0;
    width: 100%;
    border: none;
    border-bottom: 1px solid black;
}












